import { useState, useEffect } from 'react'
import Person from './components/Person'
import Filter from './components/Filter'
import Form from './components/Form'

import personService from './services/Persons'

const App = () => {

// State hooks for the app

  const [persons, setPersons] = useState([]) 
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [filterString, setFilter] = useState('')

// Effect hook for fetching data from json server

  useEffect(() => {
    personService
      .getAll()
      .then(initialNotes => {
        setPersons(initialNotes)
      })
  }, [])

// Event handlers and other variables

  const addPerson = (event) => {

    const check = persons.some(person => person.name === newName) 
    event.preventDefault()
    if (check === true) {
      if (window.confirm(`${newName} is already in the phonebook. Replace the old number with the new one?`)) {
        const edited = persons.find(n => n.id === id)
        const changedNote = { ...note, important: !note.important }

    noteService
      .update(id, changedNote)
      .then(returnedNote => {
        setNotes(notes.map(note => note.id !== id ? note : returnedNote))
      })
      }
    } else {
      const newPerson = {
        name: newName,
        number: newNumber,
      }
      personService
        .create(newPerson)
        .then(returnedPerson => {
          setPersons(persons.concat(returnedPerson))
          setNewName('')
          setNewNumber('')
        })
    }
  }

  const deletePerson = (person) => {
    if (window.confirm(`delete ${person.name}`)) {
      personService
        .deleteIt(person.id)
        setPersons(persons.filter(n => n.id !== person.id))
    }
  }

  const handleNameChange = (event) => {
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }

  const handleFilterChange = (event) => {
    setFilter(event.target.value)
  }

  const personsToShow = persons.filter(person => person.name.toLowerCase().includes(filterString.toLowerCase()))

// Rendering of webpage

  return (
    <div>
      <h2>Phonebook</h2>

      <Filter value={filterString} changeHandler={handleFilterChange} />

      <h2>Add number</h2>

      <Form
        addPerson={addPerson}
        newName={newName}
        handleNameChange={handleNameChange}
        newNumber={newNumber}
        handleNumberChange={handleNumberChange}
      />

      <h2>Numbers</h2>
      <ul>
        <ul>
          {personsToShow.map(person => 
            <Person key={person.name} person={person} deleteEntry={() => deletePerson(person)} />
          )}
        </ul>
      </ul>
    </div>
  )
}

export default App
