import { useState, useEffect } from 'react'
import axios from 'axios'
import Person from './components/Person'
import Filter from './components/Filter'
import Form from './components/Form'

const App = () => {

// State hooks for the app

  const [persons, setPersons] = useState([]) 
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [filterString, setFilter] = useState('')

// Effect hook for fetching data from json server

  useEffect(() => {
    console.log('effect')
    axios
      .get('http://localhost:3001/persons')
      .then(response => {
        console.log('promise fulfilled')
        setPersons(response.data)
      })
  }, [])
  console.log('render', notes.length, 'notes')

// Event handlers and other variables

  const addPerson = (event) => {
    const check = persons.some(person => person.name === newName) 
    event.preventDefault()
    if (check === true) {
      alert(`${newName} is already in the phonebook`)
    } else {
    const newPerson = {
      name: newName,
      number: newNumber,
      id: persons.length + 1,
    }
    setPersons(persons.concat(newPerson))
    setNewName('')
    setNewNumber('')
    }
  }

  const handleNameChange = (event) => {
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }

  const handleFilterChange = (event) => {
    setFilter(event.target.value)
  }

  const personsToShow = persons.filter(person => person.name.toLowerCase().includes(filterString.toLowerCase()))

// Rendering of webpage

  return (
    <div>
      <h2>Phonebook</h2>

      <Filter value={filterString} changeHandler={handleFilterChange} />

      <h2>Add number</h2>

      <Form addPerson={addPerson} newName={newName} handleNameChange={handleNameChange} newNumber={newNumber} handleNumberChange={handleNumberChange} />

      <h2>Numbers</h2>
      <ul>
        <ul>
          {personsToShow.map(person => 
            <Person key={person.name} person={person} />
          )}
        </ul>
      </ul>
    </div>
  )
}

export default App
